INSERT INTO `h-lesson` (`id`, `lessonname`, `coursename`, `breif`, `lessontime`) VALUES
(1, '下载并安装MonoDevelop', 'unity 3D语言入门', '本节课我们主要学习下载安装Unity 和 MonoDevelop，并会创建并第一个 unity 3D 工程运行', 7),
(2, 'unity 3D 编译过程', 'unity 3D语言入门', '本节课我们主要了解一下unity 3D的编译过程，还会掌握语句的概念以及注释的用法', 10),
(3, '二进制', 'unity 3D语言入门', '本节课我们主要了解二进制的意义，以及二进制和十进制得相互转换，还会了解八进制、十六进制', 8),
(4, '变量和常量', 'unity 3D语言入门', '本节课我们主要学习变量的声明和初始化，以及常量的使用', 12),
(5, '基本数据类型', 'unity 3D语言入门', '本节课我们主要学习中常用的数据类型，包括整型、浮点型、字符串等类型', 7),
(6, '运算符和表达式', 'unity 3D语言入门', '本节课我们主要学习中的基本运算符，以及运算符的优先级和结合性，还会了解表达式的概念', 13),
(7, '类型转换', 'unity 3D语言入门', '本节课我们主要学习不同类型之间的相互转换，包括自动转换和强制转换', 14),
(8, '使用字符串', 'unity 3D语言入门', '本节课我们主要学习字符串的用法，包括字符串拼接，转义字符等', 11),
(9, '控制台输入输出', 'IOS语言入门', '本节课我们主要学习IOS控制台输出和输入，还会学习格式化输出的方法', 11),
(10, '关系运算和逻辑运算', 'IOS语言入门', '本节课我们主要学习关系运算符和逻辑运算符，以及关系运算和逻辑运算', 12),
(11, 'Switch 分支语句', 'IOS语言入门', '本节课我们主要学习 Switch 分支语句的语法，以及 break 的使用方法', 9),
(12, 'While 和 DoWhile 循环语句', 'IOS语言入门', '本节课我们主要学习 while 和 dowhile 循环语句的用法，以及它们两者的各自使用场景', 8),
(13, 'For 循环语句', 'IOS语言入门', '本节课我们主要学习 For 循环语句的语法和特点', 15),
(14, '一维数组', 'IOS语言入门', '本节课我们主要学习一维数组的声明，初始化和使用方法', 18),
(15, '二维数组', 'IOS语言入门', '本节课我们主要学习二维数组的声明，初始化和使用方法', 14),
(16, '坦克大战', 'IOS语言入门', '本节课我们通过实践项目来复习学过的知识，要在控制台上做一个简单的游戏，能够控制坦克在场景地图中移动', 13),
(17, 'Android学习指南项目基础篇', 'Android语言入门', '对于有Java基础的朋友，可以通过本分类60讲左右的课程，打下比较坚实的基础。', 10),
(18, 'Android学习指南项目实训篇', 'Android语言入门', '对于学过“1.Android2.2学习指南基础篇”的朋友，可以通过本分类项目的联系，加深对基础知识的理解和熟练运用。', 13),
(19, 'Android学习指南项目提高篇', 'Android语言入门', '讲解Android源代码解析，OpenGL ES编程，NDK编程等专题。', 11),
(20, 'Android开发小知识', 'Android语言入门', '讲解一些Android开发中的小知识、小技巧，用以开阔视野，拓宽思路，本分类里不少内容含金量还是很高的。', 14),
(21, 'Android开发环境的搭建', 'Android语言入门', '对于有Java基础的朋友，可以通过本分类60讲左右的课程，打下比较坚实的基础。', 13),
(22, 'Android系统构架分析和应用程序目录结构分析', 'Android语言入门', '讲解一些Android开发中的小知识、小技巧，用以开阔视野，拓宽思路，本分类里不少内容含金量还是很高的。', 12),
(23, 'Android模拟器的使用', 'Android语言入门', '讲解Android源代码解析，OpenGL ES编程，NDK编程等专题。', 11),
